    //Prevenimos el comportamiento por defecto
    e.preventDefault()

    //Validación input
    if (nombre === '2525') {

      return
    }
    //Eliminar mensaje de error

  }

  return (
    <form>

      <h3>{nombre}</h3>
      <div className="form-group">
        <input className="form-control" name="nameInput" onChange={(e) => setNombre(e.target.value)} />
