import Form from 'react-bootstrap/Form';
import React, { useState } from 'react'

const Formulario = () => {
  const [emailId, setEmail] = useState("");
  const [passwd, setPassword] = useState("");

  const validarInput = (e) => {
    //Prevenimos el comportamiento por defecto
    e.preventDefault()

    //Validación input
    if (emailId === '2525') {

      return
    }
    //Eliminar mensaje de error

  }

  return (
    <form>

      <h3>{emailId} {passwd}</h3>
      <div className="form-group">
        <input className="form-control" name="emailInput" type="email" onChange={(e) => setEmail(e.target.value)} />

        {passwd === "252525" ? <Form.Control
        type="text"
        placeholder="Disabled input"
        aria-label="Disabled input example"
        disabled
        readOnly
      /> : "no"}



        <input className="form-control" name="passInput" onChange={(e) => setPassword(e.target.value)} />

        {passwd === "252525" ? <button className="btn btn-dark" type="submit"> Enviar</button> : (<button className="btn btn-dark disabled" type="submit"> Enviar</button>)}

      </div>
    </form>
  )
}

export default Formulario;
