import React, {useState} from 'react'

const Formulario = () => {
  const [nombre, setNombre] = useState("")
  const [error, setError] = useState(false)
  const [opcion, setVerificar] = useState(false);

  const validarInput = (e) => {
    //Prevenimos el comportamiento por defecto
    e.preventDefault()

    //Validación input
    if(nombre === '2525') {
      //setError(true)
      setVerificar(true);
      return
    }
    //Eliminar mensaje de error
    setError(false)
    setVerificar(false)
  }

    return (
      <form onChange={validarInput}>

        <h3>{nombre}</h3>
        <div className="form-group">
            <input className="form-control" name="Nombre" onChange={(e) => setNombre(e.target.value) }/> 
            {opcion ? <button className="btn btn-dark enabled" type="submit"> Enviar</button> : (<button className="btn btn-dark disabled" type="submit"> Enviar</button>)}
        </div>
      </form>
    )
}

export default Formulario
